import React, { Component } from 'react';
import Card from '@material-ui/core/Card';
import { CardActionArea, CardMedia, CardContent, Typography } from '@material-ui/core';

// ({ images, title })
// article link: https://jeb.biologists.org/content/221/12/jeb179598
// {this.props.images.map((value) => <img style={imageStyle} src={value} alt="Photos of projects" />)}

export default class ProjectCard extends Component {
    constructor(props) {
        super(props);
        this.state = { shadow: 1 }
    }

    onMouseOver = () => this.setState({ shadow: 3 });
    onMouseNot = () => this.setState({ shadow: 1 });

    render() {
        return (
            <Card maxWidth="345">
                <CardActionArea>
                    <CardMedia component="img" alt="Project card" image={this.props.images} />
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="h2">
                            {this.props.title}
                        </Typography>
                    </CardContent>
                </CardActionArea>
            </Card>
        )
    }
}
