import React, { Component } from 'react';
import { Card } from 'material-ui/Card';

const CardStyle = {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
}

const imageStyle = {
    height: "50vh",
    borderRadius: "24px",
    margin: "8px",
}
// ({ images, title })

export default class ProjectCard extends Component {
    constructor(props) {
        super(props);
        this.state = { shadow: 1 }
    }

    onMouseOver = () => this.setState({ shadow: 3 });
    onMouseNot = () => this.setState({ shadow: 1 });

    render() {
        return (
            <Card >
                <h2>{title}</h2>
                <a href="https://jeb.biologists.org/content/221/12/jeb179598">
                    <div className={CardStyle}>
                        {images.map((value) => <img style={imageStyle} src={value} alt="Photos of projects" />)}
                    </div>
                </a>
            </Card>
        )
    }
}
